// SplitText Plugin - Simplified Version
// This is a basic implementation for demonstration purposes

class SplitText {
    constructor(element, options = {}) {
        this.element = element;
        this.options = { type: 'chars', ...options };
        this.chars = [];
        this.words = [];
        this.lines = [];
        
        this.split();
    }
    
    split() {
        const text = this.element.textContent;
        this.element.innerHTML = '';
        
        if (this.options.type === 'chars') {
            this.splitIntoChars(text);
        } else if (this.options.type === 'words') {
            this.splitIntoWords(text);
        } else if (this.options.type === 'lines') {
            this.splitIntoLines(text);
        }
    }
    
    splitIntoChars(text) {
        for (let i = 0; i < text.length; i++) {
            const char = text[i];
            const span = document.createElement('span');
            span.textContent = char === ' ' ? '\u00A0' : char;
            span.style.display = 'inline-block';
            this.element.appendChild(span);
            this.chars.push(span);
        }
    }
    
    splitIntoWords(text) {
        const words = text.split(' ');
        words.forEach(word => {
            const span = document.createElement('span');
            span.textContent = word;
            span.style.display = 'inline-block';
            span.style.marginRight = '0.25em';
            this.element.appendChild(span);
            this.words.push(span);
        });
    }
    
    splitIntoLines(text) {
        const lines = text.split('\n');
        lines.forEach(line => {
            const div = document.createElement('div');
            div.textContent = line;
            this.element.appendChild(div);
            this.lines.push(div);
        });
    }
}

// Make SplitText available globally
window.SplitText = SplitText;
